import { Component } from '@angular/core';
/* import { FormControl, FormGroup } from '@angular/forms'; */
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-section',
  templateUrl: './section.component.html',
  styleUrls: ['./section.component.css'],
})
export class SectionComponent {
  username = '';
  origen = '';
  destino = '';
  nombre = '';
  data: any;
  resultados: any[] = [];
  consoleLog() {
    console.log(this.origen);
    console.log(this.destino);
  }
  constructor(private http: HttpClient) {
    this.origen = '';
    this.destino = '';
    this.nombre = '';
  }
  checkRoute(origin: string, destination: string, flights: any[]) {
    console.log('ESTOS SON LOS VUELOS', flights);
    let originExists = false;
    let destinationExists = false;

    for (let flight of flights[0]) {
      if (flight.departureStation === origin) {
        originExists = true;
      }
      if (flight.arrivalStation === destination) {
        destinationExists = true;
      }
    }

    if (!originExists) {
      res['msg'] = 'El origen no está registrado en la aerolínea.';
      return res;
      console.log('El origen no está registrado en la aerolínea.');
      return false;
    }

    if (!destinationExists) {
      console.log('El destino no está registrado en la aerolínea.');
      return false;
    }

    for (let flight of flights[0]) {
      if (
        flight.departureStation === origin &&
        flight.arrivalStation === destination
      ) {
        console.log('Ruta de viaje encontrada: ');
        console.log('Origen: ' + origin);
        console.log('Destino: ' + destination);
        console.log('Aerolínea: ' + flight.flightCarrier);
        console.log('Número de vuelo: ' + flight.flightNumber);
        console.log('Precio: ' + flight.price);
        return true;
      }
    }

    for (let flight of flights[0]) {
      if (flight.departureStation === origin) {
        for (let f of flights[0]) {
          if (
            f.departureStation === flight.arrivalStation &&
            f.arrivalStation === destination
          ) {
            console.log('Ruta de viaje con escalas encontrada: ');
            console.log('Origen: ' + origin);
            console.log('Destino: ' + destination);
            console.log('Aerolínea: ' + f.flightCarrier);
            console.log('Número de vuelo: ' + f.flightNumber);
            console.log('Precio: ' + (flight.price + f.price));
            return true;
          }
        }
      }
    }

    console.log(
      'No se ha encontrado una ruta de viaje entre el origen y el destino especificado.'
    );
    return false;
  }
  getData() {
    if (!this.origen || !this.destino) {
      alert('INGRESE TODOS LOS CAMPOS');
      return;
    }
    this.http.get(`https://recruiting-api.newshore.es/api/flights/2`).subscribe(
      (data) => {
        this.resultados.push(data);
        this.data = [data];
        console.log(this.data);
        this.checkRoute(this.origen, this.destino, this.data);
        this.origen = '';
        this.destino = '';
      },
      (error) => {
        console.log('ERROR', error);
        this.origen = '';
        this.destino = '';
      }
    );
  }
}
<section class="contenedorPrincipalSection">
  <img
    class="imagenPrincipal"
    src="https://cdn.pixabay.com/photo/2015/09/07/19/12/hotel-928937_1280.jpg"
    alt=""
  />
  <div class="ContendorTextoPrincipal">
    <p class="textoPrincipal">
      Reserva tu viaje con nosotros y aprovecha los descuentos que tenemos para
      ti.
    </p>
  </div>
  <hr class="lineaDivision" />

  <div class="contenedorModal">
    <div></div>
    <form class="contenedorForm" action="">
      <div>
        <h1 class="tituloModal">¿A donde quieres ir?</h1>
      </div>

      <div class="contenedorPrimerInput">
        <img class="imagenPrimerInput" src="./assets/airstart.png" alt="" />
        <input
          [(ngModel)]="origen"
          (ngModelChange)="consoleLog()"
          (ngModelChange)="origen = $event.toUpperCase()"
          [ngModelOptions]="{ standalone: true }"
          pattern="^[^0-9]*$"
          minlength="3"
          maxlength="3"
          class="input"
          type="text"
          name="Origen"
          id=""
          placeholder="Origen"
        />
      </div>
      <div class="contenedorSegundoInput">
        <img class="imagenSegundoInput" src="./assets/airend.png" alt="" />
        <input
          [(ngModel)]="destino"
          (ngModelChange)="consoleLog()"
          (ngModelChange)="destino = $event.toUpperCase()"
          [ngModelOptions]="{ standalone: true }"
          pattern="^[^0-9]*$"
          minlength="3"
          maxlength="3"
          class="input"
          type="text"
          name="Destino"
          id=""
          placeholder="Destino"
        />
      </div>

      <button (click)="getData()" class="contenedorBtnBuscar">Buscar</button>
    </form>
  </div>
</section>

